create table if not exists Posts( ID int auto_increment primary key, Title varchar(255) not null, Tagline varchar(512) not null, Image varchar(512) null, Splash varchar(512) null, Theme_Color char(7) null, Link varchar(512) not null, Author varchar(256) not null, Content text not null, Date datetime not null, Tags text null );
create table if not exists Users( Username varchar(255) not null, Password varchar(255) not null, CookieID char(255) not null, Email varchar(255) not null, ID int auto_increment primary key, constraint users_CookieID_uindex unique (CookieID), constraint users_Email_uindex unique (Email), constraint users_Username_uindex unique (Username) );
create table if not exists Favourites( User_ID int not null, Post_ID int primary key not null, Date_Added datetime not null, constraint Favourites_Post_ID_uindex unique (Post_ID), constraint Favourites_Posts_ID_fk foreign key (Post_ID) references Posts (ID), constraint Favourites_Users_ID_fk foreign key (User_ID) references Users (ID) );